import{_ as t,o as d,c as e,e as r}from"./app-5a7fdd32.js";const o={},c=r('<h1 id="vs-code-如何" tabindex="-1"><a class="header-anchor" href="#vs-code-如何" aria-hidden="true">#</a> VS Code 如何</h1><h2 id="_1-常用设置" tabindex="-1"><a class="header-anchor" href="#_1-常用设置" aria-hidden="true">#</a> 1. 常用设置</h2><table><thead><tr><th>设置项目</th><th>功能</th></tr></thead><tbody><tr><td><code>editor.renderWhitespace</code></td><td>显示空白符号</td></tr><tr><td><code>editor.bracketPairColorization.enabled</code></td><td>括号彩色高亮</td></tr><tr><td><code>editor.guides.bracketPairs</code></td><td>括号配对高亮</td></tr><tr><td><code>editor.inlineSuggest.enabled</code></td><td>行内提示</td></tr><tr><td><code>workbench.startupEditor</code></td><td>启动时打开的文件</td></tr><tr><td><code>terminal.integrated.defaultProfile.windows</code></td><td>默认终端</td></tr><tr><td><code>markdown.preview.doubleClickToSwitchToEditor</code></td><td>双击切换到编辑器</td></tr><tr><td><code>markdown.preview.scrollEditorWithPreview</code></td><td>预览时滚动编辑器</td></tr><tr><td><code>terminal.integrated.cursorStyle</code></td><td>光标样式</td></tr></tbody></table><h2 id="_2-设置快捷键" tabindex="-1"><a class="header-anchor" href="#_2-设置快捷键" aria-hidden="true">#</a> 2. 设置快捷键</h2><p>按快捷键 <code>Ctrl + K + S</code> 或者打开 <strong>文件(F)</strong> -&gt; <strong>首选项</strong> -&gt; <strong>键盘快捷方式</strong>，可以打开键盘快捷键设置界面，需要的功能搜索即可。</p><p>有一些常用功能没有指定快捷键，可以手动设置，例如我常用的快捷键设置。</p><table><thead><tr><th>功能</th><th>快捷键</th></tr></thead><tbody><tr><td>Python: 运行 Python 文件</td><td><code>Ctrl + D + R</code></td></tr><tr><td>转换为大写</td><td><code>Ctrl + D + U</code></td></tr><tr><td>转换为小写</td><td><code>Ctrl + D + L</code></td></tr><tr><td>转换为驼峰大小写</td><td><code>Ctrl + D + C</code></td></tr></tbody></table><p>搜索 <strong>用户</strong> 可以看到自定义的全部快捷键。</p>',8),a=[c];function i(n,h){return d(),e("div",null,a)}const l=t(o,[["render",i],["__file","how-to.html.vue"]]);export{l as default};
